import {Pagination, SearchIcon, Table, Theme, TrashIcon} from 'client-library';
import {PAGE_SIZE} from '../../../constants.ts';
import {FilterInput} from '../../accounting/styles.tsx';
import {FilterDropdown, Filters} from '../../budget/planning/budgetList/styles.ts';
import {tableHeadsProceduralCostsOverview} from './constants.tsx';
import {Header} from './styles.ts';
import {useState} from 'react';
import {useDebounce} from '../../../utils/useDebounce.ts';
import useAppContext from '../../../context/useAppContext.ts';
import useGetProceduralCosts from '../../../services/graphQL/proceduralCosts/useGetProceduralCosts.ts';
import useDeleteProceduralCost from '../../../services/graphQL/proceduralCosts/useDeleteProceduralCost.ts';
import {defaultDropdownOption, TypeOfFines} from '../fines/constants.tsx';
import {ConfirmationModal} from '../../../shared/confirmationModal/confirmationModal.tsx';
import {ProceduralCostOverviewItem} from '../../../types/graphQL/proceduralCosts.ts';

const initialValues = {
  procedure_cost_type_id: defaultDropdownOption.id,
};

const ProceduralCostsOverview = () => {
  const [page, setPage] = useState(1);
  const [filters, setFilters] = useState(initialValues);
  const [search, setSearch] = useState('');
  const [showDeleteProceduralCostModal, setShowDeleteProceduralCostModal] = useState<number | null>(null);

  const debouncedSearch = useDebounce(search, 500);
  const {
    navigation: {navigate},
    alert,
  } = useAppContext();

  const {proceduralCosts, total, refetch} = useGetProceduralCosts({
    page: page,
    size: PAGE_SIZE,
    ...filters,
    search: debouncedSearch,
  });
  const {deleteProceduralCost} = useDeleteProceduralCost();

  const handleDeleteProceduralCost = async () => {
    if (!showDeleteProceduralCostModal) return;
    await deleteProceduralCost(
      showDeleteProceduralCostModal,
      () => {
        refetch();
        alert.success('Uspješno obrisano.');
      },
      () => alert.error('Greška. Brisanje nije uspjelo.'),
    );
    setShowDeleteProceduralCostModal(null);
  };

  const onSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  };

  const onFilterChange = (value: any, name: string) => {
    setFilters({...filters, [name]: value?.id});
  };

  const onPageChange = (page: number) => {
    setPage(page + 1);
  };

  const filterDropdownOptions = [defaultDropdownOption, ...TypeOfFines] || [];

  return (
    <>
      <Header>
        <Filters>
          <FilterDropdown
            name="procedure_cost_type_id"
            value={filterDropdownOptions.find(option => option.id === filters?.procedure_cost_type_id)}
            onChange={(value: any) => onFilterChange(value, 'procedure_cost_type_id')}
            label="VRSTA TROŠKOVA POSTUPKA:"
            options={filterDropdownOptions}
          />

          <FilterInput
            label="PRETRAGA:"
            rightContent={<SearchIcon />}
            name="search"
            onChange={onSearch}
            value={search}
          />
        </Filters>
      </Header>
      <Table
        tableHeads={tableHeadsProceduralCostsOverview}
        data={proceduralCosts}
        style={{marginBottom: 22}}
        emptyMessage={'Još uvjek nema troškova postupka.'}
        onRowClick={(row: ProceduralCostOverviewItem) => navigate(`/finance/fines-taxes/procedural-costs/${row.id}`)}
        tableActions={[
          {
            name: 'delete',
            onClick: row => setShowDeleteProceduralCostModal(row.id),
            icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
          },
        ]}
      />
      <Pagination
        pageCount={total ? Math.ceil(total / PAGE_SIZE) : 1}
        onChange={onPageChange}
        variant="filled"
        itemsPerPage={PAGE_SIZE}
        pageRangeDisplayed={3}
        style={{marginTop: '20px'}}
      />
      <ConfirmationModal
        open={!!showDeleteProceduralCostModal}
        subTitle={'Ovaj trošak postupka će biti trajno izbrisan iz sistema.'}
        onClose={() => setShowDeleteProceduralCostModal(null)}
        onConfirm={() => handleDeleteProceduralCost()}
      />
    </>
  );
};

export default ProceduralCostsOverview;

import {Controller, useForm} from 'react-hook-form';
import {Dropdown, Datepicker, Input, Typography, FileUpload, Button} from 'client-library';
import {Container, Row} from './styles.ts';
import {feeSubcategoryOptions, feeTypeOptions, generateDropdownOptions, requiredError} from '../../../../constants.ts';
import {useEffect, useMemo, useState} from 'react';
import Footer from '../../../../shared/footer.ts';
import useGetCountOverview from '../../../../services/graphQL/counts/useGetCountOverview.ts';
import useAppContext from '../../../../context/useAppContext.ts';
import {parseDateForBackend} from '../../../../utils/dateUtils.ts';
import {yupResolver} from '@hookform/resolvers/yup';
import * as yup from 'yup';
import FileList from '../../../../components/fileList/fileList.tsx';
import {optionsNumberSchema} from '../../../../utils/formSchemas.ts';
import {FeeInsertParams} from '../../../../types/graphQL/fees.ts';
import useInsertFee from '../../../../services/graphQL/fees/useInsertFee.ts';
import {Fee} from '../constants.tsx';

const feeSchema = yup.object().shape({
  fee_type: optionsNumberSchema.required(requiredError).default(null),
  fee_subcategory: optionsNumberSchema.required(requiredError).default(null),
  decision_number: yup.string().required(requiredError),
  decision_date: yup.date().required(requiredError),
  subject: yup.string().required(requiredError),
  jmbg: yup
    .string()
    .matches(/^(0[1-9]|[12][0-9]|3[01])(0[1-9]|1[0-2])(\d{3})(2[1-9]|29)\d{3}\d{1}$/, 'Neispravan JMBG format')
    .length(13, 'JMBG mora da ima 13 cifara')
    .required(requiredError),
  residence: yup.string().required(requiredError),
  amount: yup.number().typeError('Morate unijeti broj').required(requiredError),
  payment_reference_number: yup.string().required(requiredError),
  debit_reference_number: yup.string().required(requiredError),
  execution_date: yup.date().required(requiredError),
  payment_deadline_date: yup.date().required(requiredError),
  description: yup.string(),
  court_account: optionsNumberSchema.nullable().default(null),
});

const defaultValues = {
  fee_type: undefined,
  fee_subcategory: undefined,
  decision_number: '',
  decision_date: undefined,
  subject: '',
  jmbg: '',
  account_id: undefined,
  residence: '',
  amount: 0,
  payment_reference_number: '',
  debit_reference_number: '',
  execution_date: undefined,
  payment_deadline_date: undefined,
  description: '',
  court_account: undefined,
};

type FeeEntryForm = yup.InferType<typeof feeSchema>;
export interface FeeFormProps {
  fee?: Fee;
}
const FeeForm = ({fee}: FeeFormProps) => {
  const {
    control,
    register,
    handleSubmit,
    reset,
    formState: {errors},
  } = useForm<FeeEntryForm>({resolver: yupResolver(feeSchema), defaultValues: defaultValues});
  const [uploadedFile, setUploadedFile] = useState<FileList>();
  const {counts} = useGetCountOverview({});
  const {insertFee} = useInsertFee();
  const {
    alert,
    fileService: {uploadFile},
    navigation: {navigate},
  } = useAppContext();

  const countsDropdownOptions = useMemo(() => {
    return generateDropdownOptions(counts);
  }, [counts]);
  const onSubmit = async (data: any) => {
    const {account, court_account, created_at, updated_at, file, status, fee_details, ...rest} = data;
    let payload: FeeInsertParams = {
      ...rest,
      amount: Number(data.amount),
      fee_type: data.fee_type.id,
      court_account: data.court_account?.id,
      fee_subcategory: data.fee_subcategory?.id,
      decision_date: parseDateForBackend(data.decision_date),
      execution_date: parseDateForBackend(data.execution_date),
      payment_deadline_date: parseDateForBackend(data.payment_deadline_date),
    };

    if (uploadedFile) {
      const formData = new FormData();
      const fileArray = Array.from(uploadedFile);

      formData.append('file', fileArray[0]);

      await uploadFile(
        formData,
        (res: any) => {
          setUploadedFile(undefined);
          payload = {...payload, file: [res[0]?.id]};
        },
        () => {
          alert.error('Greška pri čuvanju! Fajlovi nisu učitani.');
          return;
        },
      );

      insertOrUpdateFee(payload);
    } else {
      insertOrUpdateFee(payload);
    }
  };

  useEffect(() => {
    if (fee) {
      reset({
        ...fee,
        fee_type: feeTypeOptions.find(option => option?.id === fee.fee_type?.id),
        fee_subcategory: feeSubcategoryOptions.find(option => option?.id === fee.fee_type?.id),
        court_account: countsDropdownOptions?.find(count => count?.id === fee.court_account?.id),
        decision_date: new Date(fee.decision_date),
        payment_deadline_date: new Date(fee.payment_deadline_date),
        execution_date: new Date(fee.execution_date),
      });
    }
  }, [fee]);

  const handleUpload = (files: FileList) => {
    setUploadedFile(files);
  };

  const insertOrUpdateFee = async (payload: FeeInsertParams) => {
    // update fee
    if (fee && fee.id) {
      const updatedPayload = {...payload, id: fee.id};

      await insertFee(
        updatedPayload,
        () => {
          alert.success('Taksa uspješno izmijenjena');
        },
        () => {
          alert.error('Došlo je do greške prilikom izmjene takse');
        },
      );
      return;
    }

    // create fee
    await insertFee(
      payload,
      id => {
        navigate(`/finance/fines-taxes/fines/${id}`);
        alert.success('Taksa uspješno kreirana');
      },
      () => {
        alert.error('Došlo je do greške prilikom kreiranja takse');
      },
    );
  };

  return (
    <Container>
      <Row>
        <Controller
          name="fee_type"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="VRSTA TAKSE:"
              placeholder={'Odaberite vrstu takse'}
              options={feeTypeOptions}
              isRequired
              error={errors.fee_type?.message}
            />
          )}
        />
        <Controller
          name="fee_subcategory"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="POTKATEGORIJA TAKSE:"
              placeholder={'Odaberite potkategoriju takse'}
              options={feeSubcategoryOptions}
              isRequired
              error={errors.fee_subcategory?.message}
            />
          )}
        />
        <Input {...register('subject')} label="SUBJEKAT:" isRequired error={errors.subject?.message} />
      </Row>
      <Row>
        <Input {...register('jmbg')} label="JMBG:" isRequired error={errors.jmbg?.message} />
        <Input {...register('residence')} label="PREBIVALIŠTE:" isRequired error={errors.residence?.message} />
      </Row>
      <Row>
        <Input
          {...register('decision_number')}
          label="BROJ RJEŠENJA / PRESUDE:"
          isRequired
          error={errors.decision_number?.message}
        />
        <Controller
          name={'decision_date'}
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Datepicker
              name={name}
              selected={value ? new Date(value) : ''}
              label="DATUM RJEŠENJA / PRESUDE:"
              onChange={onChange}
              isRequired
              error={errors.decision_date?.message}
            />
          )}
        />
      </Row>
      <Row>
        <Input
          {...register('debit_reference_number')}
          label="POZIV NA BROJ ZADUŽENJA:"
          isRequired
          error={errors.debit_reference_number?.message}
        />
        <Input
          {...register('payment_reference_number')}
          label="POZIV NA BROJ ODOBRENJA:"
          isRequired
          error={errors.payment_reference_number?.message}
        />
      </Row>
      <Row>
        <Input
          {...register('amount')}
          label="VISINA TAKSE:"
          type={'number'}
          inputMode={'decimal'}
          leftContent={<div>€</div>}
          isRequired
          error={errors.amount?.message}
        />
        <Controller
          name="court_account"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="KONTO:"
              placeholder={'Odaberite konto'}
              options={countsDropdownOptions}
              isRequired
              error={errors.court_account?.message}
            />
          )}
        />
        {/*<Input*/}
        {/*  value={fine?.fine_fee_details.fee_amount_grace_period.toFixed(2)}*/}
        {/*  label={`2/3 KAZNE - UKOLIKO UPLATITE DO ${parseDate(*/}
        {/*    fine?.fine_fee_details.fee_amount_grace_period_due_date ?? null,*/}
        {/*  )}`}*/}
        {/*  type={'number'}*/}
        {/*  inputMode={'decimal'}*/}
        {/*  leftContent={<div style={{color: Theme.palette.gray300}}>€</div>}*/}
        {/*  disabled*/}
        {/*/>*/}
      </Row>
      <Row>
        <Controller
          name={'payment_deadline_date'}
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Datepicker
              name={name}
              selected={value ? new Date(value) : ''}
              label="ROK ZA PLAĆANJE UKUPNE TAKSE:"
              onChange={onChange}
              isRequired
              error={errors.payment_deadline_date?.message}
            />
          )}
        />
        <Controller
          name={'execution_date'}
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Datepicker
              name={name}
              selected={value ? new Date(value) : ''}
              label="DATUM IZVRŠNOSTI:"
              onChange={onChange}
              isRequired
              error={errors.execution_date?.message}
            />
          )}
        />
      </Row>
      <Row>
        <Input {...register('description')} label="OPIS:" textarea />
      </Row>

      <Row>
        <FileUpload
          icon={null}
          files={uploadedFile}
          variant="secondary"
          onUpload={handleUpload}
          note={<Typography variant="bodySmall" content="Dodaj fajl" />}
          buttonText="Učitaj"
        />
        <FileList files={(fee?.file && fee?.file) ?? []} />
      </Row>
      <Footer>
        <Button content="Odustani" variant="secondary" style={{width: 130}} onClick={() => reset()} />
        <Button content="Sačuvaj" variant="primary" onClick={handleSubmit(onSubmit)} />
      </Footer>
    </Container>
  );
};

export default FeeForm;

import {
  Typography,
  Table,
  TableHead,
  Dropdown,
  Input,
  Datepicker,
  EditIconTwo,
  Theme,
  TrashIcon,
  CheckIcon,
  CloseIcon,
} from 'client-library';
import {Amount, FinePaymentDetailsWrapper, LabeledDivider} from './styles.ts';
import {Controller, useFieldArray, useForm} from 'react-hook-form';
import * as yup from 'yup';
import {useEffect, useState} from 'react';
import {requiredError} from '../../../../constants.ts';
import {roundCurrency} from '../../../../utils/roundCurrency.ts';
import useAppContext from '../../../../context/useAppContext.ts';
import {parseDate, parseDateForBackend} from '../../../../utils/dateUtils.ts';
import {ConfirmationModal} from '../../../../shared/confirmationModal/confirmationModal.tsx';
import {Fee, FeeDetails} from '../constants.tsx';
import useGetFeesPayments from '../../../../services/graphQL/fees/feesPayments/useGetFeesPayments.ts';
import useInsertFeesPayment from '../../../../services/graphQL/fees/feesPayments/useInsertFeesPayment.ts';
import useDeleteFeesPayment from '../../../../services/graphQL/fees/feesPayments/useDeleteFeesPayment.ts';
import {FinePaymentMethods} from '../../fines/constants.tsx';

const singlePaymentSchema = yup.object().shape({
  id: yup.number().required(requiredError),
  payment_method: yup.object().shape({
    id: yup.number().required(),
    title: yup.string().required(requiredError),
  }),
  amount: yup.number().required(requiredError).min(1, 'Iznos mora biti veći od 0'),
  payment_date: yup.date().required(requiredError),
  receipt_number: yup.string().required(requiredError),
  payment_reference_number: yup.string().required(requiredError),
  debit_reference_number: yup.string().required(requiredError),
  originalID: yup.number().required(requiredError),
  fee_id: yup.number().required(),
  created_at: yup.string().optional(),
  updated_at: yup.string().optional(),
  status: yup.object().shape({
    id: yup.number().optional(),
    title: yup.string().optional(),
  }),
});

const paymentsSchema = yup.object().shape({
  payments: yup.array().of(singlePaymentSchema),
});

type PaymentEntryForm = yup.InferType<typeof paymentsSchema>;
type PaymentRow = yup.InferType<typeof singlePaymentSchema>;

interface PaymentFormProps {
  fee: Fee;
  refetchFee: () => Promise<void>;
}
const PaymentDetails = ({fee, refetchFee}: PaymentFormProps) => {
  const fineFeeDetails: FeeDetails = fee?.fee_details;
  const {payments, refetch} = useGetFeesPayments(fee?.id);
  const {insertFeePayment} = useInsertFeesPayment();
  const {deleteFeePayment} = useDeleteFeesPayment();
  const {alert} = useAppContext();

  const [editRowId, setEditRowId] = useState<number | null>(null);
  const [showDeletePaymentModal, setShowDeletePaymentModal] = useState<number | null>(null);

  const {
    control,
    register,
    handleSubmit,
    formState: {errors},
    setError,
  } = useForm<PaymentEntryForm>({});

  const {fields, append, remove} = useFieldArray({name: 'payments', control});
  const appendLastRow = () =>
    append({
      id: 0,
      payment_method: {
        id: 0,
        title: '',
      },
      amount: 0,
      payment_date: new Date(),
      receipt_number: '',
      payment_reference_number: '',
      debit_reference_number: '',
      originalID: 0,
      fee_id: fee.id,
      status: {
        id: 0,
        title: '',
      },
    });

  useEffect(() => {
    remove();
    if (payments.length) {
      payments.forEach(payment => {
        append({
          ...payment,
          // need to save original id to be able to edit/delete the payment because useFieldArray will change it
          originalID: payment.id,
          payment_method: FinePaymentMethods.find(method => method.title === payment.payment_method.title) ?? {
            id: 0,
            title: '',
          },
          payment_date: new Date(payment?.payment_date),
        });
      });
      appendLastRow();
      return;
    }

    appendLastRow();
  }, [payments]);

  const isRowDisabled = (row: any) => {
    return editRowId ? editRowId !== row.originalID : !!row.originalID;
  };

  const validateSingleRow = async (rowData: PaymentRow, id: number) => {
    try {
      // Validate using Yup directly
      await singlePaymentSchema.validate(rowData, {abortEarly: false});
      return true; // Validation passed
    } catch (err) {
      if (err instanceof yup.ValidationError) {
        err.inner.forEach(error => {
          setError(`payments.${id}.${error.path}` as never, {type: 'manual', message: error.message});
        });
      }
    }
  };

  const handleSubmitPayment = async (data: PaymentEntryForm) => {
    if (data.payments?.length) {
      const test = data.payments?.findIndex(item => item.originalID === editRowId);
      const selectedRow = test != -1 ? test : fields.length - 1;

      const isValid = await validateSingleRow(data?.payments[selectedRow], selectedRow);

      if (!isValid) return;

      const payment = data.payments![selectedRow];
      const {originalID, created_at, updated_at, status, ...updatedPayment} = payment;
      const parsedPaymentDate = parseDateForBackend(payment.payment_date);
      if (!parsedPaymentDate) return;

      const payload = {
        ...updatedPayment,
        payment_method: payment.payment_method.id,
        payment_date: parsedPaymentDate,
      };
      await insertFeePayment(
        payload,
        () => {
          refetch();
          refetchFee();
          alert.success('Uspješno sačuvano.');
          setEditRowId(null);
        },
        () => alert?.error('Greška. Promjene nisu sačuvane.'),
      );
    }
  };

  const handleDeletePayment = async () => {
    const paymentIndex = payments.findIndex(payment => payment.id === showDeletePaymentModal);
    if (!showDeletePaymentModal || paymentIndex === -1) return;
    await deleteFeePayment(
      showDeletePaymentModal,
      () => {
        remove(paymentIndex);
        refetchFee();
        alert.success('Uspješno obrisano.');
      },
      () => alert.error('Greška. Brisanje nije uspjelo.'),
    );
    setShowDeletePaymentModal(null);
  };

  const tableHeadsPayments: TableHead[] = [
    {
      title: 'Način plaćanja',
      accessor: 'payment_method',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Controller
            name={`payments.${index}.payment_method`}
            control={control}
            render={({field: {name, onChange, value}}) => (
              <Dropdown
                style={{width: '180px'}}
                name={name}
                onChange={onChange}
                value={value ?? FinePaymentMethods.find(method => method.title === value)}
                options={FinePaymentMethods}
                isDisabled={isRowDisabled(row)}
                error={isRowDisabled(row) ? '' : errors?.payments?.[index]?.payment_method?.title?.message}
              />
            )}
          />
        );
      },
    },
    {
      title: 'Iznos',
      accessor: 'amount',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Input
            {...register(`payments.${index}.amount`)}
            type="number"
            leftContent={
              <div style={{color: isRowDisabled(row) ? Theme.palette.gray300 : Theme.palette.gray800}}>€</div>
            }
            disabled={isRowDisabled(row)}
            error={errors?.payments?.[index]?.amount?.message}
          />
        );
      },
    },
    {
      title: 'Datum uplate',
      accessor: 'payment_date',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Controller
            name={`payments.${index}.payment_date`}
            control={control}
            render={({field: {name, onChange, value}}) => (
              <Datepicker
                name={name}
                onChange={onChange}
                selected={value ? new Date(value) : ''}
                disabled={isRowDisabled(row)}
                error={errors?.payments?.[index]?.payment_date?.message}
                popperProps={{
                  strategy: 'fixed', // fixes datepicker going behind other elements
                  placement: 'top',
                }}
              />
            )}
          />
        );
      },
    },
    {
      title: 'Broj izvoda',
      accessor: 'receipt_number',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Input
            {...register(`payments.${index}.receipt_number`)}
            disabled={isRowDisabled(row)}
            error={errors?.payments?.[index]?.receipt_number?.message}
            key={`payments.${index}.receipt_number`}
          />
        );
      },
    },
    {
      title: 'Poziv na broj odobrenja',
      accessor: 'payment_reference_number',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Input
            {...register(`payments.${index}.payment_reference_number`)}
            disabled={isRowDisabled(row)}
            error={errors?.payments?.[index]?.payment_reference_number?.message}
          />
        );
      },
    },
    {
      title: 'Poziv na broj zaduženja',
      accessor: 'debit_reference_number',
      type: 'custom',
      renderContents: (_item, row, index) => {
        return (
          <Input
            {...register(`payments.${index}.debit_reference_number`)}
            disabled={isRowDisabled(row)}
            error={errors?.payments?.[index]?.debit_reference_number?.message}
          />
        );
      },
    },
    {title: '', accessor: 'TABLE_ACTIONS', type: 'tableActions'},
  ];

  return (
    <>
      <Amount style={{marginTop: 10}}>
        <Typography style={{fontWeight: 600, marginBottom: 10}} variant={'bodySmall'} content={'Platiti do:'} />
        <Typography variant="bodySmall" content={parseDate(fee?.payment_deadline_date)} />
      </Amount>
      <LabeledDivider>
        <Typography style={{fontWeight: 600}} variant="bodySmall" content="DETALJI PLAĆANJA:" />
      </LabeledDivider>
      <Table
        tableHeads={tableHeadsPayments}
        data={fields}
        tableActions={[
          {
            name: 'Otkaži',
            onClick: () => setEditRowId(null),
            icon: <CloseIcon stroke={Theme?.palette?.gray800} />,
            shouldRender: row => !isRowDisabled(row) && !!row.originalID,
          },
          {
            name: 'Plati',

            onClick: handleSubmit(handleSubmitPayment),
            icon: <CheckIcon stroke={Theme?.palette?.gray800} width={'20px'} height={'20px'} />,
            shouldRender: row => !isRowDisabled(row) || !row.originalID,
          },
          {
            name: 'Izmijeni',
            onClick: row => setEditRowId(row.originalID),
            icon: <EditIconTwo stroke={Theme?.palette?.gray800} />,
            shouldRender: row => isRowDisabled(row) && !!row.originalID,
          },
          {
            name: 'Izbriši',
            onClick: row => setShowDeletePaymentModal(row.originalID),
            icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
            shouldRender: row => !!row.originalID && (!editRowId || editRowId !== row.originalID),
          },
        ]}
      />
      <FinePaymentDetailsWrapper>
        <Amount>
          <Typography style={{fontWeight: 600}} variant="bodySmall" content="UKUPNO:" />
          <Typography variant="bodySmall" content={roundCurrency(fineFeeDetails?.fee_all_payments_amount)} />
        </Amount>
        <Amount>
          <Typography style={{fontWeight: 600}} variant="bodySmall" content="PREOSTALO ZA UPLATU:" />
          <Typography variant="bodySmall" content={roundCurrency(fineFeeDetails?.fee_left_to_pay_amount)} />
        </Amount>
      </FinePaymentDetailsWrapper>
      <ConfirmationModal
        open={!!showDeletePaymentModal}
        subTitle={'Ova uplata će biti trajno izbrisana iz sistema.'}
        onClose={() => setShowDeletePaymentModal(null)}
        onConfirm={() => handleDeletePayment()}
      />
    </>
  );
};

export default PaymentDetails;

import {Pagination, SearchIcon, Table, Theme, TrashIcon} from 'client-library';
import {feeSubcategoryOptions, feeTypeOptions, PAGE_SIZE} from '../../../constants.ts';
import useGetFees from '../../../services/graphQL/fees/useGetFees.ts';
import {FilterInput} from '../../accounting/styles.tsx';
import {FilterDropdown, Filters} from '../../budget/planning/budgetList/styles.ts';
import {tableHeadsTaxesOverview} from './constants.tsx';
import {Header} from './styles.ts';
import {FinesOverviewItem} from '../../../types/graphQL/finesOverview.ts';
import {ConfirmationModal} from '../../../shared/confirmationModal/confirmationModal.tsx';
import {useState} from 'react';
import useAppContext from '../../../context/useAppContext.ts';
import useDeleteFee from '../../../services/graphQL/fees/useDeleteFee.ts';
import {useDebounce} from '../../../utils/useDebounce.ts';
import {defaultDropdownOption} from '../fines/constants.tsx';

const initialValues = {
  fee_type_id: undefined,
  fee_subcategory_id: undefined,
};
const TaxesOverview = () => {
  const [page, setPage] = useState(1);
  const [filters, setFilters] = useState(initialValues);
  const [search, setSearch] = useState('');
  const debouncedSearch = useDebounce(search, 500);

  const {fees, refetch, total} = useGetFees({page: page, size: PAGE_SIZE, ...filters, search: debouncedSearch});
  const [showDeleteFeeModal, setShowDeleteFeeModal] = useState<number | null>(null);

  const {
    navigation: {navigate},
    alert,
  } = useAppContext();
  // TO DO implement the logic when the BE is done
  const {deleteFee} = useDeleteFee();

  const handleDeleteFee = async () => {
    if (!showDeleteFeeModal) return;

    await deleteFee(
      showDeleteFeeModal,
      () => {
        refetch();
        alert.success('Uspješno obrisano.');
      },
      () => {
        alert.error('Greška. Brisanje nije uspjelo.');
      },
    );
  };

  const onSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  };

  const onFilterChange = (value: any, name: string) => {
    setFilters({...filters, [name]: value?.id});
  };

  const onPageChange = (page: number) => {
    setPage(page + 1);
  };

  const filterDropdownOptionsType = [defaultDropdownOption, ...feeTypeOptions] || [];
  const filterDropdownOptionsSubcategory = [defaultDropdownOption, ...feeSubcategoryOptions] || [];

  return (
    <>
      <Header>
        <Filters>
          <FilterDropdown
            name="fee_type_id"
            value={filterDropdownOptionsType.find(option => option.id === filters?.fee_type_id)}
            onChange={(value: any) => onFilterChange(value, 'fee_type_id')}
            label="VRSTA TAKSE:"
            options={filterDropdownOptionsType}
          />
          <FilterDropdown
            name="fee_subcategory_id"
            value={filterDropdownOptionsSubcategory.find(option => option.id === filters?.fee_subcategory_id)}
            onChange={(value: any) => onFilterChange(value, 'fee_subcategory_id')}
            label="POTKATEGORIJA TAKSE:"
            options={filterDropdownOptionsSubcategory}
          />

          <FilterInput
            label="PRETRAGA:"
            rightContent={<SearchIcon />}
            name="search"
            onChange={onSearch}
            value={search}
          />
        </Filters>
      </Header>
      <Table
        tableHeads={tableHeadsTaxesOverview}
        data={fees}
        style={{marginBottom: 22}}
        emptyMessage={'Još uvjek nema taksi'}
        onRowClick={(row: FinesOverviewItem) => navigate(`/finance/fines-taxes/taxes/${row.id}`)}
        tableActions={[
          {
            name: 'delete',
            onClick: row => setShowDeleteFeeModal(row.id),
            icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
          },
        ]}
      />
      <Pagination
        pageCount={total ? Math.ceil(total / PAGE_SIZE) : 1}
        onChange={onPageChange}
        variant="filled"
        itemsPerPage={PAGE_SIZE}
        pageRangeDisplayed={3}
        style={{marginTop: '20px'}}
      />
      <ConfirmationModal
        open={!!showDeleteFeeModal}
        subTitle={'Ova taksa će biti trajno izbrisana iz sistema.'}
        onClose={() => setShowDeleteFeeModal(null)}
        onConfirm={() => handleDeleteFee()}
      />
    </>
  );
};

export default TaxesOverview;

