import {yupResolver} from '@hookform/resolvers/yup';
import {Button, Dropdown, Typography} from 'client-library';
import {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import * as yup from 'yup';
import BudgetLimitModal from '../../../../components/budgetLimitModal/budgetLimitModal.tsx';
import {UserRole} from '../../../../constants.ts';
import useAppContext from '../../../../context/useAppContext.ts';
import useGetBudgets from '../../../../services/graphQL/getBudgets/useGetBudgets.ts';
import useInsertBudget from '../../../../services/graphQL/insertBudget/useInsertBudget.ts';
import BudgetTable from '../../../../shared/budgetTable/budgetTable.tsx';
import {BudgetTableStep} from '../../../../shared/budgetTable/types.ts';
import {FlexRow} from '../../../../shared/flex.ts';
import Footer from '../../../../shared/footer.ts';
import {optionsNumberSchema, optionsStringSchema} from '../../../../utils/formSchemas.ts';
import {getYearOptions} from '../../../../utils/getYearOptions.ts';
import {budgetTypeOptions} from '../budgetList/constants.tsx';
import {MainTitle, OverviewBox, ScreenWrapper} from '../budgetList/styles.ts';
import {BoldText, Box, Controls, TableGrid} from './SSSBudgetDetails.styles.ts';

export type LimitObject = {
  organization_unit_id: number;
  limit: number;
};

const addBudgetSchema = yup.object().shape({
  year: optionsStringSchema.required('Ovo polje je obavezno').default(null),
  budget_type: optionsNumberSchema.required('Ovo polje je obavezno').default(null),
  limits: yup
    .array()
    .of(
      yup.object().shape({
        limit: yup.number().required(),
        organization_unit_id: yup.number().required(),
      }),
    )
    .required()
    .default(null),
});

export type AddBudgetFormType = yup.InferType<typeof addBudgetSchema>;

const SSSBudgetDetails = () => {
  const [limitModal, setLimitModal] = useState(false);

  const {
    navigation: {
      location: {pathname},
      navigate,
    },
    contextMain,
    alert,
  } = useAppContext();

  const {
    control,
    formState: {errors, isValid},
    handleSubmit,
    watch,
    setValue,
  } = useForm<AddBudgetFormType>({resolver: yupResolver(addBudgetSchema), mode: 'onBlur'});

  const budgetID = pathname.split('/').at(-1);
  const isNew = budgetID === 'add-new';

  const {insertBudget, loading: isSaving} = useInsertBudget();
  const {budgets} = useGetBudgets({id: isNew ? null : parseInt(budgetID)});

  //todo check if the same api endpoint is used when the OJ manager is filling the budget
  const onSubmit = async (data: AddBudgetFormType) => {
    if (isSaving) return;

    const payload = {
      id: isNew ? null : parseInt(budgetID),
      year: data.year.id.toString(),
      budget_type: data.budget_type.id,
      limits: data.limits,
    };

    await insertBudget(
      payload,
      () => {
        alert.success('Uspješno sačuvano.');
        navigate('/finance/budget/planning');
      },
      () => alert?.error('Greška. Promjene nisu sačuvane.'),
    );
  };

  const onLimitModalSubmit = (data: LimitObject[]) => {
    setValue('limits', data, {shouldValidate: true});
  };

  const availableYearsForBudget = useMemo(() => {
    if (!budgets) return [];

    const years = getYearOptions(6, false, 5);

    const existingBudgetYears = budgets.items?.map(budget => budget.year) || [];
    const filteredYears = years.filter(year => !existingBudgetYears.includes(Number(year.id)));

    return filteredYears;
  }, [budgets]);

  useEffect(() => {
    if (budgets && budgets.items && budgets.items.length > 0) {
      setValue('year', {id: budgets.items[0].year.toString(), title: budgets.items[0].year.toString()});
      setValue('budget_type', budgetTypeOptions.find(option => option.id === parseInt(budgets.items[0].budget_type))!);
      setValue('limits', budgets.items[0].limits);
    }
  }, [budgets]);

  const year = watch('year')?.id;

  return (
    <ScreenWrapper>
      <OverviewBox>
        <MainTitle variant="bodyMedium" content={isNew ? 'NOVI BUDŽET' : `BUDŽET ZA ${year} GODINU`} />
        <hr />
        <Box>
          <Controls>
            <TableGrid>
              <BoldText variant="bodySmall" content="NAZIV PREDLAGAČA:" />
              <Typography variant="bodySmall" content={contextMain.organization_unit.title} />
            </TableGrid>
            <Button content="Limiti" variant="secondary" style={{width: 130}} onClick={() => setLimitModal(true)} />
          </Controls>

          <FlexRow gap="1rem" style={{marginTop: '1rem'}}>
            <Controller
              control={control}
              name="year"
              render={({field: {name, onChange, value}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={availableYearsForBudget}
                  error={errors.year?.message}
                  placeholder="Odaberite godinu"
                  isDisabled={!isNew}
                />
              )}
            />
            <Controller
              control={control}
              name="budget_type"
              render={({field: {name, onChange, value}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={budgetTypeOptions}
                  error={errors.year?.message}
                  placeholder="Odaberite tip"
                  isDisabled={!isNew}
                />
              )}
            />
          </FlexRow>
        </Box>

        {contextMain.role_id === UserRole.MANAGER_OJ && (
          <Box>
            <TableGrid>
              <BoldText variant="bodySmall" content="PROGRAM:" />
              <Typography variant="bodySmall" content="SUDSKI SAVJET" />
              <BoldText variant="bodySmall" content="POTPROGRAM:" />
              <Typography variant="bodySmall" content="SUDSKI SAVJET" />
              <BoldText variant="bodySmall" content="AKTIVNOSTI:" />
              <Typography variant="bodySmall" content="SUDSKI SAVJET" />
              <BoldText variant="bodySmall" content="IZVOR:" />
              <Typography variant="bodySmall" content="SUDSKI SAVJET" />
            </TableGrid>
          </Box>
        )}

        {year && <BudgetTable step={BudgetTableStep.CREATING} year={parseInt(year)} organizationUnitId={1} />}

        <Footer>
          <Button
            content="Otkaži"
            variant="secondary"
            style={{width: 130}}
            onClick={() => navigate('/finance/budget/planning')}
          />
          <Button
            content="Sačuvaj"
            variant="primary"
            style={{width: 130}}
            onClick={handleSubmit(onSubmit)}
            disabled={!isValid && isNew}
          />
        </Footer>
      </OverviewBox>

      <BudgetLimitModal open={limitModal} onClose={() => setLimitModal(false)} onSubmit={onLimitModalSubmit} />
    </ScreenWrapper>
  );
};

export default SSSBudgetDetails;

import {Button, Divider, EditIconTwo, Pagination, SendIcon, Table, Theme, TrashIcon} from 'client-library';
import {useState} from 'react';
import {PAGE_SIZE, UserRole} from '../../../../constants';
import useAppContext from '../../../../context/useAppContext';
import useDeleteBudget from '../../../../services/graphQL/deleteBudget/useDeleteBudget';
import useGetBudgets from '../../../../services/graphQL/getBudgets/useGetBudgets';
import useSendBudget from '../../../../services/graphQL/sendBudget/useSendBudget';
import {DeleteModal} from '../../../../shared/deleteModal/deleteModal';
import {NotificationsModal} from '../../../../shared/notifications/notificationsModal';
import {DropdownData} from '../../../../types/dropdownData';
import {BudgetOverviewItem} from '../../../../types/graphQL/budgetOverview';
import {getYearOptions} from '../../../../utils/getYearOptions';
import {budgetListTableHeads, budgetStatus, budgetTypeFilterOptions} from './constants';
import {Controls, FilterDropdown, Filters, Header, MainTitle, OverviewBox, ScreenWrapper} from './styles';
import {BudgetStatusOptions} from '../../../../types/config';

/*
 * This is a component used to show a list of budgets for both SSS official and managers of OUs.
 * Navigation is different for each role as well as the actions that can be performed on the budget.
 * The button for adding a new budget is only visible for the SSS officials.
 */

export interface BudgetOverviewFilters {
  year?: DropdownData<number> | null;
  budget_type?: DropdownData<string> | null;
  status?: DropdownData<string> | null;
}

const initialBudgetFilterValues = {
  year: null,
  budget_type: null,
  status: null,
};

const BudgetList = () => {
  const [showDeleteModalBudgetId, setShowDeleteModalBudgetId] = useState<number | undefined>(undefined);
  const [showSendModalBudgetId, setShowSendModalBudgetId] = useState<number | undefined>(undefined);
  const [page, setPage] = useState(1);
  const [filterValues, setFilterValues] = useState<BudgetOverviewFilters>(initialBudgetFilterValues);

  const {budgets, refetch} = useGetBudgets({
    page,
    size: PAGE_SIZE,
    status: filterValues.status ? filterValues.status.id : '',
    budget_type: filterValues.budget_type ? filterValues.budget_type.id : null,
    year: filterValues.year ? filterValues.year?.id : null,
  });

  const {deleteBudget} = useDeleteBudget();
  const {sendBudget} = useSendBudget();

  const {
    navigation: {navigate},
    alert,
    breadcrumbs,
    contextMain: {role_id},
  } = useAppContext();

  const onDelete = (budget: BudgetOverviewItem) => {
    setShowDeleteModalBudgetId(budget.id);
  };

  const onSend = (budget: BudgetOverviewItem) => {
    // TODO handle rest of the logic and modal
    // check if we also need confirmation modal for this
    setShowSendModalBudgetId(budget.id);
    handleSend();
  };

  const handleCloseSendModal = () => {
    setShowSendModalBudgetId(undefined);
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModalBudgetId(undefined);
  };

  const handleDelete = async () => {
    if (!showDeleteModalBudgetId) return;

    await deleteBudget(
      showDeleteModalBudgetId,
      () => {
        alert.success('Uspješno ste obrisali budžet');
        refetch();
      },
      () => {
        alert.error('Došlo je do greške prilikom brisanja budžeta');
      },
    );
    setShowDeleteModalBudgetId(undefined);
  };

  const handleSend = async () => {
    if (!showSendModalBudgetId) return;

    await sendBudget(
      showSendModalBudgetId,
      () => {
        alert.success('Uspješno ste poslali budžet');
      },
      () => {
        alert.error('Došlo je do greške prilikom slanja budžeta');
      },
    );

    handleCloseSendModal();
    refetch();
  };

  const onPageChange = (page: number) => {
    setPage(page + 1);
  };

  const onFilter = (value: any, name: string) => {
    setFilterValues({...filterValues, [name]: name === 'search' ? value.target.value : value});
  };

  const onRowClick = (row: BudgetOverviewItem) => {
    if (role_id === UserRole.ADMIN) {
      navigate(`/finance/budget/planning/${row.id}`);
      breadcrumbs.add({
        name: 'Detalji',
        to: `/finance/budget/planning/${row.id}`,
      });
    } else {
      navigate(`/finance/budget/planning/${row.id}/summary`);
      breadcrumbs.add({
        name: 'Summary',
        to: `/finance/budget/planning/${row.id}/summary`,
      });
    }
  };

  return (
    <ScreenWrapper>
      <OverviewBox>
        <MainTitle variant="bodyMedium" content="PREGLED BUDŽETA" />
        <Divider color="#615959" height="1px" />
        <Header>
          <Filters>
            <FilterDropdown
              label="GODINA:"
              options={getYearOptions(7, true, 1)}
              value={filterValues.year}
              name="year"
              onChange={value => onFilter(value, 'year')}
              placeholder="Odaberite godinu"
            />
            <FilterDropdown
              label="TIP BUDŽETA:"
              options={budgetTypeFilterOptions}
              value={filterValues.budget_type}
              name="type"
              onChange={value => onFilter(value, 'budget_type')}
              placeholder="Odaberite tip"
            />
            <FilterDropdown
              label="STATUS:"
              options={BudgetStatusOptions}
              value={filterValues.status}
              name="status"
              onChange={value => onFilter(value, 'status')}
              placeholder="Odaberite status"
            />
          </Filters>
          <Controls>
            {role_id === UserRole.ADMIN && (
              <Button
                content="Novi budžet"
                variant="secondary"
                style={{width: 130}}
                onClick={() => navigate('/finance/budget/planning/add-new')}
              />
            )}
          </Controls>
        </Header>
        <Table
          tableHeads={budgetListTableHeads}
          data={budgets.items}
          style={{marginBottom: 22}}
          tableActions={[
            {
              name: 'Pošalji',
              onClick: onSend,
              icon: <SendIcon stroke={Theme?.palette?.gray800} />,
              shouldRender: row => row.status === 'Kreiran',
            },
            {
              name: 'Izmijeni',
              onClick: row => navigate(`/finance/${row.budget_type}/budget-create-${row.year}/${row.id}`),
              icon: <EditIconTwo stroke={Theme?.palette?.gray800} />,
              shouldRender: row => row.status === 'Kreiran',
            },
            {
              name: 'Izbriši',
              onClick: onDelete,
              icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
              shouldRender: row => row.status !== 'Obradi',
            },
          ]}
          onRowClick={row => onRowClick(row)}
        />
        <DeleteModal
          open={!!showDeleteModalBudgetId}
          onClose={() => {
            handleCloseDeleteModal();
          }}
          handleDelete={handleDelete}
        />
        <NotificationsModal
          open={!!showSendModalBudgetId}
          onClose={handleCloseSendModal}
          handleRightButtomClick={() => handleSend()}
          subTitle={'Ovaj budžet će biti poslat organizacionim jedinicama na pregled i popunjavanje.'}
        />
        <Pagination
          pageCount={budgets.total ? Math.ceil(budgets.total / PAGE_SIZE) : 0}
          onChange={onPageChange}
          variant="filled"
          itemsPerPage={PAGE_SIZE}
          pageRangeDisplayed={3}
          style={{marginTop: '20px'}}
        />
      </OverviewBox>
    </ScreenWrapper>
  );
};

export default BudgetList;

import {Controller, useForm} from 'react-hook-form';
import {FinesFormContainer, Row, Amount, LabeledDivider} from './styles.ts';
import {mockDropdownOptions} from '../../../../constants.ts';
import {Dropdown, Datepicker, Input, Button, Typography, Table} from 'client-library';
import {useEffect} from 'react';
import Footer from '../../../../shared/footer.ts';
import {tableHeadsAddFines} from '../constants.ts';

const initialValues = {
  vrsta_akta: null,
  potkategorija_akta: null,
  broj_resenja: null,
  datum_rjesenja: null,
  subjekat: null,
  jmbg: null,
  prebivaliste: null,
  visina_kazne: 0,
  dvije_trecine_kazne: 0,
  poziv_na_broj_odobrenja: null,
  poziv_na_broj_zaduzenja: null,
  konto: null,
  opis_konta: null,
  datum_izvrsnosti: null,
  rok_za_placanje: null,
  opis: null,
};
const AddConfiscation = () => {
  const {control, register, handleSubmit, reset, watch, setValue} = useForm({defaultValues: initialValues});
  const onSubmit = (data: any) => {
    // TODO add submit api call
    console.log(data);
  };

  const visina_kazne = watch('visina_kazne');

  useEffect(() => {
    if (!visina_kazne) return;
    setValue('dvije_trecine_kazne', Math.ceil((visina_kazne * 2) / 3));
  }, [visina_kazne]);

  return (
    <FinesFormContainer>
      <Row>
        <Controller
          name="vrsta_akta"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="VRSTA AKTA:"
              placeholder={'Odaberite vrstu akta'}
              options={mockDropdownOptions}
            />
          )}
        />
        <Controller
          name="potkategorija_akta"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="POTKATEGORIJA AKTA:"
              placeholder={'Odaberite potkategoriju akta'}
              options={mockDropdownOptions}
            />
          )}
        />
      </Row>
      <Row>
        <Input
          {...register('broj_resenja')}
          label="BROJ RJEŠENJA / PRESUDE:"
          type={'number'}
          inputMode={'decimal'}
          leftContent={<div>€</div>}
        />
        <Controller
          name={'datum_rjesenja'}
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Datepicker
              name={name}
              selected={value ? new Date(value) : ''}
              label="DATUM RJEŠENJA:"
              onChange={onChange}
            />
          )}
        />
      </Row>
      <Row>
        <Input {...register('subjekat')} label="Subjekat:" />
      </Row>
      <Row>
        <Input {...register('jmbg')} label="JMBG:" />
        <Input {...register('prebivaliste')} label="PREBIVALIŠTE:" />
      </Row>
      <Row>
        <Input
          {...register('visina_kazne')}
          label="VISINA KAZNE:"
          type={'number'}
          inputMode={'decimal'}
          leftContent={<div>€</div>}
        />
        <Input
          {...register('dvije_trecine_kazne')}
          label="2/3 KAZNE:"
          type={'number'}
          inputMode={'decimal'}
          leftContent={<div>€</div>}
        />
      </Row>
      <Row>
        <Input {...register('poziv_na_broj_odobrenja')} label="POZIV NA BROJ ODOBRENJA:" />
        <Input {...register('poziv_na_broj_zaduzenja')} label="POZIV NA BROJ ZADUŽENJA:" />
      </Row>
      <Row>
        <Controller
          name="konto"
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Dropdown
              name={name}
              value={value}
              onChange={onChange}
              label="KONTO:"
              placeholder={'Odaberite KONTO'}
              options={mockDropdownOptions}
            />
          )}
        />
        <Input {...register('opis_konta')} label="OPIS KONTA:" />
      </Row>
      <Row>
        <Controller
          name={'datum_izvrsnosti'}
          control={control}
          render={({field: {name, value, onChange}}) => (
            <Datepicker
              name={name}
              selected={value ? new Date(value) : ''}
              label="DATUM IZVRŠOSTI:"
              onChange={onChange}
            />
          )}
        />{' '}
        <Controller
          name={'rok_za_placanje'}
          control={control}
          render={({field: {name, value, onChange}}) => (
